		}
	});
}

cur_frm.set_query("item_code", "custom_materials", function(frm, cdt, cdn) {
	return {
	    filters:{"company": cur_frm.doc.company, "disabled":0, "is_stock_item": 1}
	}
});

cur_frm.set_query("warehouse", "custom_materials", function(frm, cdt, cdn) {
	return {
	    filters:{"company": cur_frm.doc.company, "disabled":0, "is_group": 0}
	}
});

