frappe.ui.form.on('Packing Slip Item', {
    warehouse(frm,cdt,cdn){
      set_balance(frm,cdt,cdn)  
    },
    item_code(frm,cdt,cdn) {
        var d = locals[cdt][cdn];
        console.log("before_delay")
        set_balance(frm,cdt,cdn)
    }
});


frappe.ui.form.on('Packing Slip Consumed', {
    warehouse(frm,cdt,cdn){
      set_balance(frm,cdt,cdn)  
    },
    item_code(frm,cdt,cdn) {
        var d = locals[cdt][cdn];
        console.log("before_delay")
        set_balance(frm,cdt,cdn)
    },

});



var set_balance = function(frm,cdt,cdn){
    var d = locals[cdt][cdn];
    var warehouse = d.warehouse;
    if(d.item_code && warehouse){
        frappe.call({
          method: 'frappe.client.get_value',
          args: {
            doctype: 'Bin',
            filters: {
              'item_code': d.item_code,
              'warehouse': warehouse
            },
            fieldname: ['actual_qty']
          },
          callback: function (data) {
              if(data.message){
                frappe.model.set_value(cdt,cdn,"availability",data.message.actual_qty)
              }
              else{
                  frappe.model.set_value(cdt,cdn,"availability",0)
              }
          }
        });
    }
}
